import React, { useState, useRef } from "react";
import "./App.css";

const GRID_SIZE = 20; // Grid is always 20x20, only zoom level changes
const CELL_SIZE = 40; // Each cell size in pixels

const App = () => {
  const [scale, setScale] = useState(1); // Zoom level
  const [offset, setOffset] = useState({ x: 0, y: 0 }); // Grid position
  const [dragging, setDragging] = useState(false);
  const [lastMouse, setLastMouse] = useState(null);
  const containerRef = useRef(null);

  // Handle Zoom In/Out
  const handleZoom = (zoomIn) => {
    setScale((prev) => Math.max(0.5, Math.min(2, zoomIn ? prev * 1.2 : prev / 1.2)));
  };

  // Start Dragging
  const handleMouseDown = (e) => {
    setDragging(true);
    setLastMouse({ x: e.clientX, y: e.clientY });
  };

  // Drag the grid
  const handleMouseMove = (e) => {
    if (!dragging) return;
    const dx = e.clientX - lastMouse.x;
    const dy = e.clientY - lastMouse.y;
    setOffset((prev) => ({ x: prev.x + dx, y: prev.y + dy }));
    setLastMouse({ x: e.clientX, y: e.clientY });
  };

  // Stop Dragging
  const handleMouseUp = () => setDragging(false);

  return (
    <div className="container">
      <h1>Warehouse Grid Simulation</h1>
      <div className="controls">
        <button onClick={() => handleZoom(false)}>➖ Zoom Out</button>
        <button onClick={() => handleZoom(true)}>➕ Zoom In</button>
      </div>
      <div
        className="grid-container"
        ref={containerRef}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
      >
        <div
          className="grid"
          style={{
            transform: `translate(${offset.x}px, ${offset.y}px) scale(${scale})`,
            transformOrigin: "center",
          }}
        >
          {[...Array(GRID_SIZE)].map((_, rowIndex) =>
            [...Array(GRID_SIZE)].map((_, colIndex) => (
              <div key={`${rowIndex},${colIndex}`} className="cell"></div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
