import React, { useState, useRef } from "react";
import "./App.css";

const GRID_SIZE = 20; // 20x20 grid
const CELL_SIZE = 40; // Each cell is 40px

const App = () => {
  const [scale, setScale] = useState(1); // Zoom level
  const [offset, setOffset] = useState({ x: 0, y: 0 }); // Grid movement
  const [dragging, setDragging] = useState(false);
  const [selectedCells, setSelectedCells] = useState(new Set()); // Store selected cells
  const dragStart = useRef({ x: 0, y: 0 });

  // Handle zoom in/out
  const handleZoom = (zoomIn) => {
    setScale((prev) => Math.max(0.5, Math.min(2, zoomIn ? prev * 1.2 : prev / 1.2)));
  };

  // Handle double click to enable dragging
  const handleDoubleClick = () => {
    setDragging(true);
  };

  // Handle dragging movement
  const handleMouseMove = (e) => {
    if (!dragging) return;
    const dx = e.clientX - dragStart.current.x;
    const dy = e.clientY - dragStart.current.y;
    setOffset((prev) => ({ x: prev.x + dx, y: prev.y + dy }));
    dragStart.current = { x: e.clientX, y: e.clientY };
  };

  // Stop dragging
  const handleMouseUp = () => {
    setDragging(false);
  };

  // Handle single click for selecting a cell
  const handleCellClick = (row, col) => {
    if (dragging) return; // Ignore clicks while dragging
    const cellKey = `${row},${col}`;
    setSelectedCells((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(cellKey)) {
        newSet.delete(cellKey); // Remove if already selected
      } else {
        newSet.add(cellKey); // Add if not selected
      }
      return newSet;
    });
  };

  return (
    <div className="container">
      <h1>Warehouse Grid Simulation</h1>
      <div className="controls">
        <button onClick={() => handleZoom(false)}>âž– Zoom Out</button>
        <button onClick={() => handleZoom(true)}>âž• Zoom In</button>
      </div>
      <div
        className="grid-container"
        onDoubleClick={handleDoubleClick}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
      >
        <div
          className="grid"
          style={{
            transform: `translate(${offset.x}px, ${offset.y}px) scale(${scale})`,
            transformOrigin: "center",
          }}
        >
          {Array.from({ length: GRID_SIZE }, (_, rowIndex) =>
            Array.from({ length: GRID_SIZE }, (_, colIndex) => {
              const cellKey = `${rowIndex},${colIndex}`;
              return (
                <div
                  key={cellKey}
                  className={`cell ${selectedCells.has(cellKey) ? "selected" : ""}`}
                  onClick={() => handleCellClick(rowIndex, colIndex)}
                  onMouseDown={(e) => {
                    dragStart.current = { x: e.clientX, y: e.clientY };
                  }}
                >
                  {selectedCells.has(cellKey) ? "ðŸ”‹" : ""}
                </div>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
