import React, { useState, useEffect } from "react";
import { getRobotPositions } from "./api";
import "./App.css";

const GRID_SIZE = 20;

const App = () => {
  const [zoom, setZoom] = useState(1);
  const [objects, setObjects] = useState({});
  const [robots, setRobots] = useState([]); // Store robot positions

  useEffect(() => {
    const interval = setInterval(async () => {
      const positions = await getRobotPositions();
      setRobots(positions);
    }, 1000); // Fetch positions every second
    return () => clearInterval(interval);
  }, []);

  const handleZoom = (factor) => {
    setZoom((prevZoom) => Math.max(0.5, Math.min(2, prevZoom * factor)));
  };

  const handleClick = (row, col) => {
    const key = `${row},${col}`;
    const objectTypes = ["QR", "Robot", "Charging"];
    const currentType = objects[key] || null;
    const nextType = objectTypes[(objectTypes.indexOf(currentType) + 1) % objectTypes.length];
    setObjects({ ...objects, [key]: nextType });
  };

  return (
    <div className="container">
      <div className="controls">
        <button onClick={() => handleZoom(1.2)}>Zoom In</button>
        <button onClick={() => handleZoom(0.8)}>Zoom Out</button>
      </div>

      <div className="grid-container" style={{ transform: `scale(${zoom})` }}>
        {[...Array(GRID_SIZE)].map((_, row) =>
          [...Array(GRID_SIZE)].map((_, col) => {
            const key = `${row},${col}`;
            const isRobot = robots.some(robot => robot.x === col && robot.y === row);
            return (
              <div key={key} className="cell" onClick={() => handleClick(row, col)}>
                {isRobot ? <span className="robot">ðŸ¤–</span> : objects[key] && <span className="object">{objects[key]}</span>}
              </div>
            );
          })
        )}
      </div>
    </div>
  );
};

export default App;
