import React, { useState, useRef } from "react";
import "./App.css";

const GRID_SIZE = 20; // Grid size (20x20)
const CELL_SIZE = 40; // Each cell is 40px

const App = () => {
  const [scale, setScale] = useState(1); // Zoom level
  const [offset, setOffset] = useState({ x: 0, y: 0 }); // Grid movement
  const [dragging, setDragging] = useState(false);
  const [gridData, setGridData] = useState(
    Array(GRID_SIZE).fill(null).map(() => Array(GRID_SIZE).fill(null))
  );
  const gridRef = useRef(null);
  const dragStart = useRef({ x: 0, y: 0 });

  // Handle zoom in/out
  const handleZoom = (zoomIn) => {
    setScale((prev) => Math.max(0.5, Math.min(2, zoomIn ? prev * 1.2 : prev / 1.2)));
  };

  // Handle double click to enable dragging
  const handleDoubleClick = () => {
    setDragging(true);
  };

  // Handle mouse move for dragging
  const handleMouseMove = (e) => {
    if (!dragging) return;
    const dx = e.clientX - dragStart.current.x;
    const dy = e.clientY - dragStart.current.y;
    setOffset((prev) => ({ x: prev.x + dx, y: prev.y + dy }));
    dragStart.current = { x: e.clientX, y: e.clientY };
  };

  // Stop dragging when mouse is released
  const handleMouseUp = () => setDragging(false);

  // Handle single click on a grid cell to toggle an element
  const handleCellClick = (row, col) => {
    if (dragging) return; // Ignore click while dragging
    setGridData((prevGrid) => {
      const newGrid = [...prevGrid];
      newGrid[row][col] = newGrid[row][col] ? null : "ðŸ”‹"; // Toggle element
      return newGrid;
    });
  };

  return (
    <div className="container">
      <h1>Warehouse Grid Simulation</h1>
      <div className="controls">
        <button onClick={() => handleZoom(false)}>âž– Zoom Out</button>
        <button onClick={() => handleZoom(true)}>âž• Zoom In</button>
      </div>
      <div
        className="grid-container"
        onDoubleClick={handleDoubleClick}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
      >
        <div
          ref={gridRef}
          className="grid"
          style={{
            transform: `translate(${offset.x}px, ${offset.y}px) scale(${scale})`,
            transformOrigin: "center",
          }}
        >
          {gridData.map((row, rowIndex) =>
            row.map((cell, colIndex) => (
              <div
                key={`${rowIndex},${colIndex}`}
                className="cell"
                onClick={() => handleCellClick(rowIndex, colIndex)}
                onMouseDown={(e) => {
                  dragStart.current = { x: e.clientX, y: e.clientY };
                }}
              >
                {cell}
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
