import React, { useEffect, useState } from "react";
import axios from "axios";

const App = () => {
  const [status, setStatus] = useState("Loading...");

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/api/status")
      .then((response) => setStatus(response.data.message))
      .catch((error) => setStatus("Error connecting to simulation"));
  }, []);

  return (
    <div>
      <h1>Warehouse Simulation</h1>
      <p>{status}</p>
      <iframe src="http://127.0.0.1:5000" width="600" height="600" title="Simulation"></iframe>
    </div>
  );
};

export default App;
