import React, { useState } from "react";
import "./App.css";

const GRID_SIZE = 20; // Default grid size (adjustable for zoom)

const App = () => {
  const [gridSize, setGridSize] = useState(GRID_SIZE);
  const [grid, setGrid] = useState(
    Array(gridSize).fill().map(() => Array(gridSize).fill(null))
  );

  const handleClick = (row, col) => {
    const type = prompt("Enter object type (Robot / Charging / QR Code):");
    if (type) {
      const newGrid = [...grid];
      newGrid[row][col] = type;
      setGrid(newGrid);
    }
  };

  const zoomIn = () => setGridSize((prev) => Math.min(prev + 5, 50));
  const zoomOut = () => setGridSize((prev) => Math.max(prev - 5, 10));

  return (
    <div className="container">
      <h1>Warehouse Grid Simulation</h1>
      <div className="controls">
        <button onClick={zoomOut}>➖ Zoom Out</button>
        <button onClick={zoomIn}>➕ Zoom In</button>
      </div>
      <div className="grid" style={{ gridTemplateColumns: `repeat(${gridSize}, 20px)` }}>
        {grid.map((row, rowIndex) =>
          row.map((cell, colIndex) => (
            <div key={`${rowIndex}-${colIndex}`} className="cell" onClick={() => handleClick(rowIndex, colIndex)}>
              {cell && <span>{cell}</span>}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default App;
