import React, { useState, useRef, useEffect } from "react";
import "./App.css";

const GRID_SIZE = 20; // Grid size (20x20)
const CELL_SIZE = 40; // Each cell is 40px

const App = () => {
  const [scale, setScale] = useState(1); // Zoom level
  const [offset, setOffset] = useState({ x: 0, y: 0 }); // Grid movement
  const [dragging, setDragging] = useState(false);
  const [spacePressed, setSpacePressed] = useState(false); // Space bar for dragging
  const [selectedCells, setSelectedCells] = useState(new Set()); // Store selected cells
  const dragStart = useRef({ x: 0, y: 0 });

  // Handle zoom in/out
  const handleZoom = (zoomIn) => {
    setScale((prev) => Math.max(0.5, Math.min(2, zoomIn ? prev * 1.2 : prev / 1.2)));
  };

  // Handle mouse movement while dragging
  const handleMouseMove = (e) => {
    if (!dragging) return;
    const dx = e.clientX - dragStart.current.x;
    const dy = e.clientY - dragStart.current.y;
    setOffset((prev) => ({ x: prev.x + dx, y: prev.y + dy }));
    dragStart.current = { x: e.clientX, y: e.clientY };
  };

  // Stop dragging
  const handleMouseUp = () => {
    setDragging(false);
  };

  // Start dragging when Space Bar is pressed
  const handleMouseDown = (e) => {
    if (spacePressed) {
      setDragging(true);
      dragStart.current = { x: e.clientX, y: e.clientY };
    }
  };

  // Handle cell selection
  const handleCellClick = (row, col) => {
    if (spacePressed) return; // Prevent selecting while dragging

    const cellKey = `${row},${col}`;
    setSelectedCells((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(cellKey)) {
        newSet.delete(cellKey); // Deselect
      } else {
        newSet.add(cellKey); // Select
      }
      return newSet;
    });
  };

  // Detect Space Bar press & release
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.code === "Space" && !spacePressed) {
        setSpacePressed(true);
      }
    };

    const handleKeyUp = (e) => {
      if (e.code === "Space") {
        setSpacePressed(false);
        setDragging(false); // Stop dragging when Space Bar is released
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, [spacePressed]);

  return (
    <div className="container">
      <h1>Warehouse Grid Simulation</h1>
      <div className="controls">
        <button onClick={() => handleZoom(false)}>âž– Zoom Out</button>
        <button onClick={() => handleZoom(true)}>âž• Zoom In</button>
        <p>Hold <b>Space Bar</b> to move the map</p>
      </div>
      <div
        className={`grid-container ${spacePressed ? "grabbing" : ""}`}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseDown={handleMouseDown}
      >
        <div
          className="grid"
          style={{
            transform: `translate(${offset.x}px, ${offset.y}px) scale(${scale})`,
            transformOrigin: "center",
          }}
        >
          {Array.from({ length: GRID_SIZE }, (_, rowIndex) =>
            Array.from({ length: GRID_SIZE }, (_, colIndex) => {
              const cellKey = `${rowIndex},${colIndex}`;
              return (
                <div
                  key={cellKey}
                  className={`cell ${selectedCells.has(cellKey) ? "selected" : ""}`}
                  onClick={() => handleCellClick(rowIndex, colIndex)}
                >
                  {selectedCells.has(cellKey) ? "ðŸ”‹" : ""}
                </div>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
