import React, { useState } from "react";
import "./App.css";

const INITIAL_GRID_SIZE = 20;

const App = () => {
  const [gridSize, setGridSize] = useState(INITIAL_GRID_SIZE);
  const [objects, setObjects] = useState({}); // Store objects using (row, col) as key

  const handleClick = (row, col) => {
    const type = prompt("Enter object type (Robot / Charging / QR Code):");
    if (type) {
      setObjects({ ...objects, [`${row},${col}`]: type });
    }
  };

  const zoomIn = () => setGridSize((prev) => Math.min(prev + 5, 50));
  const zoomOut = () => setGridSize((prev) => Math.max(prev - 5, 10));

  return (
    <div className="container">
      <h1>Warehouse Grid Simulation</h1>
      <div className="controls">
        <button onClick={zoomOut}>➖ Zoom Out</button>
        <button onClick={zoomIn}>➕ Zoom In</button>
      </div>
      <div className="grid" style={{ gridTemplateColumns: `repeat(${gridSize}, 20px)` }}>
        {[...Array(gridSize)].map((_, rowIndex) =>
          [...Array(gridSize)].map((_, colIndex) => {
            const key = `${rowIndex},${colIndex}`;
            return (
              <div key={key} className="cell" onClick={() => handleClick(rowIndex, colIndex)}>
                {objects[key] && <span>{objects[key]}</span>}
              </div>
            );
          })
        )}
      </div>
    </div>
  );
};

export default App;
